import React, { Component, useState } from 'react';


import { RichText } from 'prismic-reactjs';


let TrackSingle = props => {


	const [selectedTrackNumber] = useState(0);

	return (
		<li  onClick={ () => props.setTrack(props.trackName, props.trackFile) } className={props.trackNumber + (selectedTrackName === props.trackFile ? ' tracklist-card__list-active-track' : '')}>
		 	<i className="material-icons" style={{fontSize: '20px'}}>play_arrow</i> <span className="mb-0">{RichText.asText(props.trackName)}</span>
		</li>
	)
}

export default TrackSingle;
