import Link from 'next/link';
import { getHomePageAPI, getBlogPostsAPI } from '../api';
// We create this in just a tick
import { linkResolver } from '../helpers';

import { RichText } from 'prismic-reactjs';



/**
* As you can see we are importing our layout here,
* and below we wrap all of the page in it.
*/
import DefaultLayout from '../layouts';

let profileCardStyles = profileCardImage => {
  return (
      {
        backgroundImage: 'url(' + profileCardImage + ')',
        backgroundSize: 'cover',
        paddingTop: '100%'
      }
  )
}

let profileCardText = () => {
  return (
    {
        color: 'white',
        fontWeight: '700',
        fontFamily: 'Montserrat',
        position: 'absolute',
        top: '40%',
        left: '50%',
        transform: 'translateX(-50%) translateY(-50%)'
    }
  )
}



const Index = ({ home = [] }) => (
  <DefaultLayout>
      {home.map(homepage => (

        <div>
         <div className="row">
         {homepage.data.body.filter(slice => slice.slice_type == "profile_card").map(profile => {
            profile = profile.primary;
            return (
               <div className="profile-card col-md-6 p-0"> 
               <style>{`       
                  .profile-card {
                      background-color: #000;
                      background-image: url(https://www.transparenttextures.com/patterns/asfalt-light.png);
                  } 
                `}</style>                              
                  <div className="profile-card__image text-center" style={profileCardStyles()}>
                      <div className="profile-card__text text-center mb-5" style={profileCardText()}>
                        <h1 className="profile-card__title display-1"><img src={profile.profile_logo.url} width="400" alt={RichText.asText(profile.profile_title)} /></h1>
                      </div>
                  </div>
               </div>
            )
          })}
          {homepage.data.body.filter(slice => slice.slice_type == "tracklist").map(tracklist => (
              <div className="tracklist-card col-md-6 px-0">
              <style>{`       
                  .tracklist-card {
                      background-color: #000;
                      color: #fff;
                      background-image: url(https://www.transparenttextures.com/patterns/asfalt-light.png);
                  } 
              `}</style>     
                  <div className="tracklist-card__current p-5">
                      <i class="material-icons">play_circle_outline</i>
                  </div>

                  <ul className="list-group">
                  {tracklist.items.map( (track, index) => (
                         <li className="list-group-item p-4">
                            <h4 className="pb-3">{RichText.asText(track.track_title)}</h4>
                          {track.track_mp3_file && 
                             <audio controls>
                                <source src={track.track_mp3_file.url} type="audio/mpeg"/>
                                Your browser does not support the audio element.
                              </audio>
                          }
                          {track.purchase_link && <div className="mt-3 d-none"><a className="btn btn-primary" href={track.purchase_link.url} target="_blank">Purchase</a></div>}
                        </li>
                      )
                  )}
                  </ul>         
              </div>
          ))}  
         </div>
        </div>
      ))}
  </DefaultLayout>
);

Index.getInitialProps = async () => {
  // Here we call the API and request 5 documents
  const response = await getHomePageAPI({ pageSize: 1 });
  return {
    home: response.results
  };
};

export default Index;
