import React, { Component } from 'react';

import { RichText } from 'prismic-reactjs';

let TrackSingle = props => {

	const [selectedTrackName, selectedTrackFile] = useState(0);


	return (
		<li  onClick={ () => props.setTrack(props.trackName, props.trackFile) } className={props.state.selectedTrackFile === props.trackFile && 'tracklist-card__list-active-track'}>
		 	<i className="material-icons" style={{fontSize: '20px'}}>play_arrow</i> <span className="mb-0">{RichText.asText(props.trackName)}</span>
		</li>
	)
}

export default TrackSingle;
