           <div className="row">
              {homepage.data.body.map(slice => slice.slice_type == "profile_card" && <ProfileCard profile={slice.primary} />)}              
              {homepage.data.body.map(slice => slice.slice_type == "tracklist" && <TracksCard trackSource={slice.items} />)}  
           </div>
        </div>
      ))}
  </DefaultLayout>
);

Index.getInitialProps = async () => {
  // Here we call the API and request 5 documents
  const response = await getHomePageAPI({ pageSize: 1 });
  return {
    home: response.results
  };
};

*/

export default Index;
