import Link from 'next/link';
import { getHomePageAPI, getBlogPostsAPI } from '../api';
// We create this in just a tick
import { linkResolver } from '../helpers';

import { RichText } from 'prismic-reactjs';
/**
* As you can see we are importing our layout here,
* and below we wrap all of the page in it.
*/
import DefaultLayout from '../layouts';

const Index = ({ home = [] }) => (
  <DefaultLayout>
      {home.map((homepage, index) => (

        <div key={index}>
         <div className="row">
         {homepage.data.body.find(slice => slice.slice_type == "profile_card").primary.map(profile => (

            <div className="profile-card col-md-6">
                <div className="profile-card__title">
                  <h1 className="display-1">{RichText.asText(profile.profile_title)}</h1>
                </div>
            </div>

          ))}
            
            <div className="tracklist-card col-md-6">
              TRACKLIST
            </div>
         </div>
         <div>{JSON.stringify(homepage.data)}</div>         
        </div>
      ))}
  </DefaultLayout>
);

Index.getInitialProps = async () => {
  // Here we call the API and request 5 documents
  const response = await getHomePageAPI({ pageSize: 1 });
  return {
    home: response.results
  };
};

export default Index;
