import Link from 'next/link';
import { getHomePageAPI, getBlogPostsAPI } from '../api';
// We create this in just a tick
import { linkResolver } from '../helpers';

import { RichText } from 'prismic-reactjs';

import ReactPlayer from 'react-player';

import React, { Component } from 'react'

import ProfileCard from '../components/ProfileCard.js';
import TracksCard from '../components/TracksCard.js';

/**
* As you can see we are importing our layout here,
* and below we wrap all of the page in it.
*/
import DefaultLayout from '../layouts';



function getTime(time) {
  if (!isNaN(time)) {
    return (
      Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2)
    );
  }
}


class Index extends React.Component {
    state = {
      selectedTrack: null,
      player: "stopped",
      currentTime: null,
      duration: null
    };


    componentDidMount() {
      this.player.addEventListener("timeupdate", e => {
        this.setState({
          currentTime: e.target.currentTime,
          duration: e.target.duration
        });
      });
    }

    componentWillUnmount() {
      this.player.removeEventListener("timeupdate", () => {});
    }

    componentDidUpdate(prevProps, prevState) {
      if (this.state.selectedTrack !== prevState.selectedTrack) {
        let track;
        switch (this.state.selectedTrack) {
          case "I'm Feeling It":
            track = 'https://prismic-io.s3.amazonaws.com/heequalsmcsqaured/f767c674-8925-418e-921c-49b7dbd0ac1d_I%27m+Feelin+It.mp3';
            break;
          case "Celebration":
            track = 'https://prismic-io.s3.amazonaws.com/heequalsmcsqaured/90dec21a-b627-4ccc-bd1d-0124cf9d447a_Celebration.mp3';
            break;
          default:
            break;
        }
        if (track) {
          this.player.src = track;
          this.player.play();
          this.setState({ player: "playing", duration: this.player.duration });
        }
      }
      if (this.state.player !== prevState.player) {
        if (this.state.player === "paused") {
          this.player.pause();
        } else if (this.state.player === "stopped") {
          this.player.pause();
          this.player.currentTime = 0;
          this.setState({ selectedTrack: null });
        } else if (
          this.state.player === "playing" &&
          prevState.player === "paused"
        ) {
          this.player.play();
        }
      }
    }


  static async getInitialProps() {
    const response = await getHomePageAPI({ pageSize: 1 });
     return {
      home: response.results
    };
  }

  render() {


    //this.props.home.map(homepage => {

        const list = [
          { id: 1, title: "I'm Feeling It" },
          { id: 2, title: "Celebration" }
        ].map(item => {
          return (
            <li
              key={item.id}
              onClick={() => this.setState({ selectedTrack: item.title })}
            >
              {item.title}
            </li>
          );

    //  }

    });

    const currentTime = getTime(this.state.currentTime);
    const duration = getTime(this.state.duration);


    return (
        <DefaultLayout>
                <div className="row">
                    {homepage.data.body.map(slice => slice.slice_type == "profile_card" && <ProfileCard profile={slice.primary} />)}              
                    {homepage.data.body.map(slice => slice.slice_type == "tracklist" && <TracksCard state={this} trackSource={slice.items} />)}  
                 </div>
            {this.props.home.map(homepage => (
              <div>
              <ul>{list}</ul>
              <div>
                {this.state.player === "paused" && (
                  <button onClick={() => this.setState({ player: "playing" })}>
                    Play
                  </button>
                )}
                {this.state.player === "playing" && (
                  <button onClick={() => this.setState({ player: "paused" })}>
                    Pause
                  </button>
                )}
                {this.state.player === "playing" || this.state.player === "paused" ? (
                  <button onClick={() => this.setState({ player: "stopped" })}>
                    Stop
                  </button>
                ) : (
                  ""
                )}
              </div>
              {this.state.player === "playing" || this.state.player === "paused" ? (
                <div>
                  {currentTime} / {duration}
                </div>
              ) : (
                ""
              )}

                  <audio ref={ref => (this.player = ref)} />
              </div>
            ))}
        </DefaultLayout>
    )
  }
}


export default Index;
