import Link from 'next/link';
import { getHomePageAPI, getBlogPostsAPI } from '../api';
// We create this in just a tick
import { linkResolver } from '../helpers';

import { RichText } from 'prismic-reactjs';

import ReactPlayer from 'react-player';

import React, { Component } from 'react'

import TrackList from '../components/TrackList.js';


/**
* As you can see we are importing our layout here,
* and below we wrap all of the page in it.
*/
import DefaultLayout from '../layouts';


let profileCardStyles = profileCardImage => {
  return (
      {
        backgroundImage: 'url(' + profileCardImage + ')',
        backgroundSize: 'cover',
        paddingTop: '100%'
      }
  )
}

let profileCardText = () => {
  return (
    {
        color: 'white',
        fontWeight: '700',
        fontFamily: 'Montserrat',
        position: 'absolute',
        top: '40%',
        left: '50%',
        transform: 'translateX(-50%) translateY(-50%)'
    }
  )
}




const Index = ({ home = [] }) => (
  <DefaultLayout>
      {home.map(homepage => (

        <div>
         <div className="row">
         {homepage.data.body.filter(slice => slice.slice_type == "profile_card").map(profile => {
            profile = profile.primary;
            return (
               <div className="profile-card col-md-6 p-0"> 
               <style>{`       
                  .profile-card {
                      background-color: #212121;
                      background-image: url(https://www.transparenttextures.com/patterns/asfalt-light.png);
                  } 
                `}</style>                              
                  <div className="profile-card__image text-center" style={profileCardStyles()}>
                      <div className="profile-card__text text-center mb-5" style={profileCardText()}>
                        <h1 className="profile-card__title display-1"><img src={profile.profile_logo.url} width="400" alt={RichText.asText(profile.profile_title)} /></h1>
                      </div>
                  </div>
               </div>
            )
          })}
          {homepage.data.body.filter(slice => slice.slice_type == "tracklist").map(tracklist => (
              <div className="tracklist-card col-md-6 px-0">
              <div className="tracklist-card__inner mx-auto shadow">
              <style>{`       
                 .tracklist-card {
                      background-color: #f7f7f7;
                      color: #fff;
                      background-image: url(https://www.transparenttextures.com/patterns/asfalt-light.png);
                  }

                  .tracklist-card__inner {
                      width: 90%;
                      margin-top: 50px
                  }

                    .tracklist-card__current {
                        padding: 20px;
                        padding-top: 20px;
                        border-radius: 3px;
                        background-color: #212121;
                    }

                  .tracklist-card__current-cover {
                      width: 100px;
                      height: 100px;
                      background-color: #c14343;
                      position: relative;
                      background-image: url("https://cdn.beatstars.com/eyJidWNrZXQiOiJidHMtY29udGVudCIsImtleSI6InVzZXJzL…l0cyI6eyJyZXNpemUiOnsiZml0IjpudWxsLCJ3aWR0aCI6MjAwLCJoZWlnaHQiOjIwMH19fQ==");
                      background-size: cover;
                      border-radius: 2px;
                  }

                 .tracklist-card__current-action {
                      position: absolute;
                      top: 50%;
                      left: 50%;
                      transform: translate(-50%, -50%);
                      text-shadow: 0 .5rem 1rem rgba(0,0,0,.15) !important;
                      cursor: pointer;
                      transition: .3s ease all;
                      line-height: 80%;
                  }

                  .tracklist-card__current-action:hover {
                    top: 48%
                  }

                  .tracklist-card__current-cover-holder {
                      position: relative;
                      margin-right: 20px
                  }

                  .tracklist-card__navigate-previous, .tracklist-card__navigate-next {
                    cursor: pointer;
                    transition: .3s ease-in-out all;
                    position: relative
                  }

                  .tracklist-card__navigate-previous {
                    margin-right: 30px
                  }

                  .tracklist-card__navigate-previous:hover {
                    left:-1px
                  }  

                  .tracklist-card__navigate-next:hover {
                    right:-1px
                  }           

                  .tracklist-card__current-title {
                     font-family: 'Montserrat';
                     font-weight: 500
                  }

                  .tracklist-card__progress .progress-bar {
                      background-color: #c14343;
                  }

                  .tracklist-card__progress:after {
                      content: "";width: 15px;
                      height: 15px;
                      background: #c14343;
                      border-radius: 100%;
                      position: absolute;
                      left: 25%;
                      top: -6px;
                      z-index: 99
                  }

                  .tracklist-card__progress {
                      position: relative;
                  }

                  .tracklist-card__list .list-group-item {
                      background-color: transparent;
                      border-bottom: 1px solid #fff;
                      display: flex;
                      align-items: center;
                  }

                  .tracklist-card__list .list-group-item i {
                      margin-right: 25px;
                  }

                  .tracklist-card__list ul li {
                      color: #353535;
                      list-style: none;
                      padding: 20px;
                      display: flex;
                      align-items: center;
                      background: #fff;
                      border-bottom: 1px solid rgba(0,0,0,.1);
                      cursor: pointer
                  }

                  .tracklist-card__list ul li:hover {
                    background-color: #f7f7f7
                  }

                  .tracklist-card__list ul li.tracklist-card__list-active-track {
                      background-color: #e7e7e7;
                  }

                  .tracklist-card__list ul {
                      padding: 0;
                      margin: 0;
                  }

                  .tracklist-card__list ul li i {
                      margin-right: 20px;
                  }

                  .tracklist-card__list ul li span {
                      font-weight: 500;
                      font-size: 16px;
                  }
              `}</style>

                  <div className="tracklist-card__current">
                  {tracklist.items.filter((track, index) => index === 0).map( (track, index) => {

                      return (
                        <div>
                           <audio />
                            <div className="tracklist-card__current-cover-holder d-inline-block">                          
                             <div className="tracklist-card__current-cover">
                                <div className="tracklist-card__current-action text-shadow"><i className="material-icons" style={{fontSize: '40px'}}>play_circle_outline</i></div>
                             </div>
                          </div>
                          <div className="tracklist-card__current-text d-inline-block">
                            <p className="tracklist-card__current-title h5">{RichText.asText(track.track_title)}</p>
                            <p className="tracklist-card__current-time" mp3File={track.track_mp3_file.url} purchaseLink={track.purchase_link.url}>{'0:00 / 3:06'}</p>
                            <div className="tracklist-card__navigate">
                                <i className="tracklist-card__navigate-previous material-icons" style={{fontSize: '20px'}}>fast_rewind</i>
                                <i className="tracklist-card__navigate-next material-icons" style={{fontSize: '20px'}}>fast_forward</i>
                            </div>
                          </div>
                          <div className="tracklist-card__progress mt-3">
                            <div className="progress" style={{height: '3px'}}>
                              <div className="progress-bar" role="progressbar" style={{width: '25%'}} aria-valuenow="25" aria-valuemin="0" aria-valuemax="100"></div>
                            </div>
                          </div>
                        </div>
                      )
                   })}
                  </div>

                  <TrackList listSource={tracklist.items} />
                
                </div>
              </div>
          ))}  
         </div>
        </div>
      ))}
  </DefaultLayout>
);

Index.getInitialProps = async () => {
  // Here we call the API and request 5 documents
  const response = await getHomePageAPI({ pageSize: 1 });
  return {
    home: response.results
  };
};

export default Index;
