import React, { Component, useState } from 'react';


import { RichText } from 'prismic-reactjs';


let TrackSingle = props => {


	const [selectedTrackNumber, setTrackNumber] = useState('track_3');

	const handleTrackChange = () => {
		setTrackNumber()
	}

	return (
		<li  onClick={ () => props.setTrack(props.trackName, props.trackFile, props.trackNumber) } className={handleTrackChange == 'track_5' + (selectedTrackNumber === props.trackNumber ? ' tracklist-card__list-active-track' : '')}>
		 	<i className="material-icons" style={{fontSize: '20px'}}>play_arrow</i> <span className="mb-0">{RichText.asText(props.trackName)}</span>
		</li>
	)
}

export default TrackSingle;
